// SPDX-License-Identifier: MIT
pragma solidity >=0.6.0 <0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract Token is ERC20 {
    address public minter;

    event MinterChanged(address from, address to);

    constructor() public payable ERC20("Decentralized Bank Currency", "DBC") {
        minter = msg.sender;
    }

    function passMinterRole(address dBank) public {
        require(
            msg.sender == minter,
            "Error, only owner can pass the minter role"
        );
        minter = dBank;

        emit MinterChanged(msg.sender, dBank);
    }

    function mint(address account, uint256 amount) public {
        require(msg.sender == minter, "Error, only the minter can mint tokens");
        _mint(account, amount);
    }
}
